mapper.select:
select bno, title, content, writer, regDate, viewCnt
 from pPost
  where bno = #{bno}

mapper.update:
update pPost
 set
  title = #{title},
  content = #{content}
 where bno = #{bno}

mapper.select:
select bno, title, content, writer, regDate, viewCnt
   from (
       select bno, title, content, writer, regDate, viewCnt,
           row_number() over(order by bno desc) as rNum
       from pPost
 ) mb
where rNum between #{rowStart} and #{rowEnd}
    order by bno desc
